import { useRef  , useEffect} from 'react'
import './statics/css/Menu.css'

var Menu = props=>{
    var ref = useRef({})

    var login = props.login

    var open = props.open

    let showHide = useCallback(
        (item ) => {
            if (!open){
                ref.current["menu_box"].style.display = 'none'
            }else if (open){
                ref.current["menu_box"].style.display = 'flex'
            }
        },
        [],
    )


    
    
    


    return(
        <div className="menu_box" id = "menu_box" ref= {showHide }>
            <div className="menu_container" id = "menu_container" ref = { el => ref.current['menu_container']}>
                

            </div>
        </div>
    )
}


export default Menu;
