import anime from 'animejs/lib/anime.es.js'
import './statics/css/appHeader.css'
import blogTubeIcon1 from './statics/images/blogTubeIcon.svg'

import menuIcon from  './statics/images/menu_icon.svg'
import menuIcon3 from  './statics/images/menu_icon3.svg'
import searchIcon from './statics/images/search_icon.svg'

import { useState ,useRef , useEffect} from 'react'

const AppHeader = props=>{
    var [img_state , set_img_state] = useState(menuIcon)

    var menuIcon_elem = useRef({})

    var blog_list = ['sample1','sample2','sample3','sample4','sample5']
    
    var mq = window.matchMedia('(max-width: 768px)')
    mq.onchange = ()=>{
        set_responsive(mq.matches)
        
    }
    var [responsive , set_responsive] = useState(mq.matches)
    
    
    let menu_click = ()=>{
        console.log(menuIcon_elem )
        if (menuIcon_elem.current['menuIcon'].alt == 'menuIcon'){
            menuIcon_elem.current['menuIcon'].src = menuIcon3 
            menuIcon_elem.current['menuIcon'].alt = 'menuIcon3'
        }else{
            menuIcon_elem.current['menuIcon'].src = menuIcon
            menuIcon_elem.current['menuIcon'].alt = 'menuIcon'
        }

    }
    var responsiveSearch  = ()=>{
        
    }
    var blog_list_div = document.getElementById('blogList')
    var hideSearch=()=>{
        
    }

    
    
    return(
        <div className="header">
            <div className="menuicon"  >
                <img src={img_state} id = 'menuIcon' ref = {el => menuIcon_elem.current['menuIcon'] = el}  onClick = {menu_click} alt="menuIcon" />
                
            </div>
            <h1 id = 'blog_tube' > <img src = {blogTubeIcon1}  id  = 'logo' />
            
            Blog Tube</h1>

            { responsive ? <img src = {searchIcon} id= 'searchIcon' onClick= {responsiveSearch} />
            :<><input   ref = {el => menuIcon_elem.current['searchIcon']= el}  placeholder = 'Search Blogs' id = 'searchInput' onBlur= {hideSearch} type = 'text' />            </>
            }
            <div id = "blogList" >
                {blog_list.length !== 0?blog_list.map((e, i , a)=><div key = {i+a.length} className = 'blog_elems'>{e}</div>)
                :<div  className = 'blog_elems'> No Blogs Found</div>}
            </div>
            {responsive?
            <div className="search" ref = {el=>menuIcon_elem.current['search'] = el}>
                {responsive?
                <><input   ref = {el => menuIcon_elem.current['searchIconResponsive']= el}  placeholder = 'Search Blogs' id = 'searchInputResponsive' type = 'text' />            </> :null }
                <div id="blogListContainer">
                { blog_list.length !== 0?blog_list.map((e, i , a)=><div key = {i} className = 'blog_elems'>{e}</div>)
                :<div  className = 'blog_elems'> No Blogs Found</div>}
                </div>
            </div>:null
}
        </div>
    )
}

export default AppHeader
