import './statics/css/appLogin.css'
import username_logo from './statics/images/username.svg'
import password_logo from './statics/images/password.svg'
import form_background from './statics/images/form_background.svg'
import appConfig from './statics/appConfig.json'

import anime from 'animejs'
import { useEffect, useRef , useState , useCallback} from 'react'
import {
    Link, Redirect 
} from 'react-router-dom'

var AppLogin = props=>{
   
    let ref = useRef({})

    let [Error, set_Error] = useState(false)

    useEffect(()=>{
        try{
        if (!Error){
        ref.current['username'].style.backgroundImage = `url(${username_logo})`
        ref.current['password'].style.backgroundImage = `url(${password_logo})`
        ref.current['form'].style.backgroundImage = `url(${form_background})`
        anime({
            targets:ref.current['heading'],
            update:function(anime){
                try{
                ref.current['heading'].style.filter = `hue-rotate(${anime.progress *3.6}deg)`
                }catch(err){
                    console.log(err)
                }
            },
            duration:2000,
            autoplay:true,
            direction:'alternate',
            loop:true
        })}
    }catch(err){
        console.log(err)
    }


    },[ref , Error])

    const origin = appConfig.origin

    let chars = {"!":null , "#":null , "$":null,"%":null,"^":null,"&":null,"*":null,"(":null,")":null,"'":null, '"':null,";":null, ":":null}
    let Login = (event)=>{
        console.log(event)
        let form = event.target
        let email_input = form[0]
        let password_input = form[1]

        let xhr = new XMLHttpRequest()
        xhr.open('POST', `${origin}backend_api/loginmanully`)
        props.appbodyloading('flex')
        xhr.setRequestHeader("credentails" , JSON.parse({email : email_input.value,
                                                        password : password_input.value}))
        xhr.send()
    }
    var checkUsername = useCallback(event=>{
        event.preventDefault()
        let filtered_list = [...event.target.value].filter((item)=> !(item in chars))
        let list = [...event.target.value]

        if( filtered_list.join('') == list.join('') ){
                event.target.setCustomValidity("")
            }

        else if (event.nativeEvent.data === '"' || event.nativeEvent.data === "'"){
            event.target.value = event.target.value.slice(0, event.target.value.length-1)
        }else if (event.nativeEvent.data in chars ){
            event.target.setCustomValidity("username should not contain \n ------------ ?!:;@#$%^&*() ------------")
            event.target.reportValidity()
            
        }

    })

    let login_form = (<><h1 align='center' ref = {el =>ref.current['heading'] =el} >Login</h1>
    <form action= 'javascript:void(0);' method = 'POST' onSubmit =  {(e)=>Login(e) } >  
        <input id='username' maxLength={100} type="email" ref= {el=>ref.current['username']= el} placeholder = 'Email' onInput= {e=>checkUsername(e)} required = {true} />
        <input type="password" minLength={8} maxLength={100} id='password' ref = {el=>ref.current['password']=el}  placeholder= "Password" required = {true} />
        <input className='submit' type="submit"  value= 'Login'   />
    </form></>)

    let error_msg = (<div className = 'error'>
    <h2 ref = {el =>ref.current['heading'] =el} align = 'cetner'>Invalid Credentials</h2>
    <button className = "back"  onClick = {()=>set_Error(false)} >Back</button>
    </div>)

    return (
        <div className="login">
            <div className="form" ref = {el =>ref.current['form'] = el}>
                 {!Error?login_form :error_msg }

                <div className="redirectSignup">
                    <p>Didn't Have an account?<Link to = '/Signup'>SignUp</Link></p>
                </div>

                
            </div>
        </div>
    )
}


export default AppLogin;
