import {useCallback, useEffect, useRef ,useState } from 'react'
import './statics/css/Menu.css'
import anime from 'animejs/lib/anime.es'

function Menu  (props){
    var ref = useRef({})

    var login = props.login


    var menu_box;

    var ref = useRef({})
    let count = 0 

    function click_event(event){
        if (event.target === ref.current['menu_box']){
            change_display(ref.current['menu_box'])
            count+=1
            setTimeout(()=>{
                if (count===1){
                    props.onClick()
                    count=0
                }

            },300)
            event.target.removeEventListener('click' ,click_event , true)
           
            
        }
    }

    function change_display(item){
        if (!props.open){
            menu_box_anime.play()
            setTimeout(()=>{
                ref.current['menu_box'].style.display = 'none'
                
            },300)
            item.addEventListener('click' , click_event , true)
        }else{
            item.style.display = 'flex'
            menu_box_anime_reverse.play()

        }
    }


    useEffect(()=>{
        change_display(ref.current['menu_box'])
    }, [props.open])

    let menu_box_anime = anime({
        targets : '.menu_container',
        left : [ '0px','-270px' ],
        easing : 'easeInQuad',
              
        loop : false,
        autoplay : false,
        duration:200,
        direction: 'forward'
    })
    let menu_box_anime_reverse = anime({
        targets : '.menu_container',
        left : [ '-270px','0px' ],
        easing : 'easeInQuad',
              
        loop : false,
        autoplay : false,
        duration:200,
        direction: 'forward'
    })

    let mq = window.matchMedia('(min-width: 768px)')

    window.addEventListener('resize' , ()=>{
    if (!mq.matches){
        ref.current["menu_box"].style.left = 0
    }})
    
    


    return(
        <div className="menu_box" id = "menu_box"  ref= {el => ref.current['menu_box'] = el}  >
            <div className="menu_container" id = "menu_container" ref = { el => ref.current['menu_container']}>
                

            </div>
        </div>
    )
}


export default Menu;
