import './statics/css/appSignup.css'
import {useRef , useEffect} from 'react'
import username from './statics/images/username.svg'
import password from './statics/images/password.svg'


var AppSignup = props=>{

    let chars = {"!":null , "#":null , "$":null,"%":null,"^":null,"&":null,"*":null,"(":null,")":null,"'":null, '"':null}


    function checkUsername(event) {
        event.preventDefault()
        let filtered_list = [...event.target.value].filter((item)=> !(item in chars))
        let list = [...event.target.value]

        if( filtered_list.join('') == list.join('') ){
            console.log(filtered_list, list)
                event.target.setCustomValidity("")
            }

        else if (event.nativeEvent.data === '"' || event.nativeEvent.data === "'"){
            event.target.value = event.target.value.slice(0, event.target.value.length-1)
        }else if (event.nativeEvent.data in chars ){
            event.target.setCustomValidity("username should not contain \n ------------ ?!#$%^&*() ------------")
            event.target.reportValidity()
            
        }
    }

    function removequotes(event){
        if (event.nativeEvent.data === '"' || event.nativeEvent.data === "'"){
            event.target.value = event.target.value.slice(0, event.target.value.length-1)
            event.target.setCustomValidity('Cannot use " or \' ')
            event.target.reportValidity()
        }else{
            event.target.setCustomValidity('')
            
        }
    }

    let ref = useRef({})

    // useEffect for ref
    useEffect(()=>{

        ref.current['username'].style.backgroudImage = username
        ref.current['password'].style.backgroundImage = password

    }, [ref])

 
    return(
      <div className="signup">
          <div className="signup_form"  ref = {el=>ref.current['signup_form']=el}>
            <h1 className = "signup_heading">Sign Up</h1>
            <form action="javascript:void(0);">
                <input type="text" className= 'signup_username' ref = {el => ref.current['username']= el} maxLength={100} onInput= {event=>checkUsername(event)} />
                <input type= "email" className= "signup_email"  ref = {el => ref.current['email']= el}  maxLength={100} onInput= {e=>removequotes(e)} />
                <input type="password" className= 'signup_password'  ref = {el => ref.current['password']= el}  minLength={8} onInput={e=>removequotes(e)}  />
                <input type="submit" id = "signup_submit" value="Sign Up" />
                <div className="upload_image">
                    <input type="file" id = 'file_input' accept= "image/*" />
                    <label for= "file_input" id = "file_input_label" >Upload File Here</label>
                </div>
            </form>
                
          </div>
      </div>
    )
}

export default AppSignup
