import AppBodyLoading from "./appBodyLoading";
import {useEffect , useState} from 'react';

const AppLoading = props=>{

    const [state , set_state] = useState(props.loading)

    useEffect(()=>{
        set_state(props.loading)
    }, [props.loading])

    return (<div 
    style = {{width:'100%',height:'100%',position:'absolute', left:'0',bottom:'0',display:{state}}}>
    <AppBodyLoading loading = 'flex' />
    </div>
    )
}

export default AppLoading
