import './statics/css/AppLogin.css'
import username_logo from './statics/images/username.svg'
import password_logo from './statics/images/password.svg'


import { useEffect, useRef } from 'react'

var AppLogin = props=>{
   
    let ref = useRef({})

    useEffect(()=>{
        console.log('this worked',ref.current['username'])
        ref.current['username'].style.backgroundImage = `url(${username_logo})`
        ref.current['password'].style.backgroundImage = `url(${password_logo})`

    },[ref])

    return (
        <div className="login">
            <div className="form" ref = {el =>ref.current['form'] = el}>
                <h1 align='center'>Login</h1>
                <form method = 'POST'>  
                    <input id='username' type="text" ref= {el=>ref.current['username']= el} placeholder = 'Username or Email' />
                    <input type="password"  id='password' ref = {el=>ref.current['password']=el}  placeholder= "Password" />
                    {/* <input className='login' type="submit"  value= 'Login' /> */}
                </form>
            </div>
        </div>
    )
}


export default AppLogin;
