import anime from 'animejs/lib/anime.es.js'
import './statics/css/appHeader.css'
import blogTubeIcon1 from './statics/images/blogTubeIcon.svg'

import menuIcon from  './statics/images/menu_icon.svg'
import menuIcon2 from  './statics/images/menu_icon2.svg'
import menuIcon3 from  './statics/images/menu_icon3.svg'

import { useState ,useRef} from 'react'

const AppHeader = props=>{
    var [img_state , set_img_state] = useState(menuIcon)

    var menuIcon_elem = useRef(null)

    let media_query_lg = window.matchMedia('(max-width: 768px)')

    let menu_click = ()=>{
        if (menuIcon_elem.current.alt == 'menuIcon'){
            menuIcon_elem.current.src = menuIcon3 
            menuIcon_elem.current.alt = 'menuIcon3'
        }else{
            menuIcon_elem.current.src = menuIcon
            menuIcon_elem.current.alt = 'menuIcon'

        }
        
        
    }

    
    
    return(
        <div className="header">
            <div className="menuicon"  >
                <img src={img_state} id = 'menuIcon' ref = {menuIcon_elem}  onClick = {menu_click} alt="menuIcon" />
                
            </div>
            <h1 id = 'blog_tube' > <img src = {blogTubeIcon1}  id  = 'logo' />
            
            Blog Tube</h1>

            { media_query_lg.matches ? 
            <input type = 'text' /> :
            <button></button>}

        </div>
    )
}

export default AppHeader
