import "./statics/css/appHome.css"
import UserImage from './userImage'
import PropType from 'prop-types'
import { useContext, useEffect, useState } from "react"
import appConfig from './statics/appConfig.json'
import { Login_context } from "../App"
import "./statics/css/appBlog.css"
import view_logo from './statics/images/preview.svg'
import like_logo from './statics/images/like.svg'
import dislike_logo from './statics/images/dislike.svg'

const AppHome = props=>{

    const session = useContext(Login_context)
    useEffect( ()=>{
        fetch(appConfig.origin+'backend_api/retriveBlogs',{ 
            mode:'cors',
            method:"POST",
            headers:{ session : session }
        })
        .then(response=> response.text().then(
            text=>console.log(text)
        ))
    },[])
    
    return(
        <div className="homepage">
            <BlogComponent user_details={{uid:"hello",name:"hello",date:'hello'}} views= {0} likes={0} dislikes={0} />
        </div>
    )
}
export default AppHome

const BlogComponent = props=>{
    const [style , set_style ] = useState({gridTemplateColumns: "minmax(250px , auto) minmax(40% ,auto)",
        gridTemplateRows: "60px 100px 20px"})
    return (
        <div className="blog_details" style = {style} >
            <img className="blog_image" />
            <div className="details">
                <div className="blogger">
                    <UserImage usre_id = {props.user_details.uid} width="50px" height="50px" style ={{
                        borderWidth:"2px"
                    }} />
                    <span id = "username">{props.user_details.name}</span>
                </div>
                <span className = "discription"></span>
                <span className="public_details">
                    <span id = "like_label" style = {{backgroundImage : `url(${like_logo})`,backgroundSize:"20px 20px"}} >{props.likes?props.likes:"No likes"}</span>
                    <span id = "view_label" style = {{backgroundImage : `url(${view_logo})`,backgroundSize:"20px 20px"}} >{props.views?props.views:"No views"}</span>
                    <span id = "dislike_label" style = {{backgroundImage : `url(${dislike_logo})`,backgroundSize:"20px 20px"}} >{props.dislikes?props.dislikes:"No dislikes"}</span>
                    <span className ="datetime">{props.user_details.date}</span>
                </span>
            </div>
        </div>
    )
}

BlogComponent.propTypes = {
    user_details : PropType.object.isRequired,
    views : PropType.number.isRequired,
    likes : PropType.number.isRequired,
    dislikes : PropType.number.isRequired,
    mini : PropType.bool
}
