import {useCallback, useEffect, useRef ,useState } from 'react'
import './statics/css/Menu.css'

function Menu  (props){
    var ref = useRef({})

    var login = props.login


    var menu_box;

    var ref = useRef({})

    function change_display(item){
        if (!props.open){
            item.style.display = 'none'
        }else if (props.open){
            item.style.display = 'flex'
        }
    }

    let showHide = useCallback(
        (item ) => {
            console.log(item)
            menu_box = item;
            change_display(item)
            menu_box.addEventListener('click', (event)=>{
                console.log('event fired')
                if (event.target === menu_box){
                    menu_box.style.display = "none"
                }
            })
        },
        [],
    )

    useEffect(()=>{
        change_display(ref.current['menu_box'])
    }, [props.open])
      
    
    


    return(
        <div className="menu_box" id = "menu_box"  ref= {el => ref.current['menu_box'] = el} onLoad = {() => {showHide(this)}}  >
            <div className="menu_container" id = "menu_container" ref = { el => ref.current['menu_container']}>
                

            </div>
        </div>
    )
}


export default Menu;
