import './statics/css/AppLogin.css'
import username_logo from './statics/images/username.svg'
import password_logo from './statics/images/password.svg'
import form_background from './statics/images/form_background.svg'

import anime from 'animejs'
import { useEffect, useRef , useState} from 'react'

var AppLogin = props=>{
   
    let ref = useRef({})

    let [Error, set_Error] = useStae(false)

    useEffect(()=>{
        if (!Error){
        ref.current['username'].style.backgroundImage = `url(${username_logo})`
        ref.current['password'].style.backgroundImage = `url(${password_logo})`
        ref.current['form'].style.backgroundImage = `url(${form_background})`

        anime({
            targets:ref.current['heading'],
            update:function(anime){
                ref.current['heading'].style.filter = `hue-rotate(${anime.progress *3.6}deg)`
            },
            duration:2000,
            autoplay:true,
            direction:'alternate',
            loop:true
        })}


    },[ref])

    return (
        <div className="login">
            <div className="form" ref = {el =>ref.current['form'] = el}>
                 {!Error?<><h1 align='center' ref = {el =>ref.current['heading'] =el} >Login</h1>
                <form action= 'javascript:void(0);' >  
                    <input id='username' type="text" ref= {el=>ref.current['username']= el} placeholder = 'Username or Email' />
                    <input type="password"  id='password' ref = {el=>ref.current['password']=el}  placeholder= "Password" />
                    <input className='submit' type="submit"  value= 'Login' onClick = {()=>{set_Error(true);console.log(Error);}} />
                </form></>
                :<h1>Invalid Credentials</h1>}
                
            </div>
        </div>
    )
}


export default AppLogin;
