import './statics/css/appFooter.css'
import {useRef , useEffect} from 'react'

var AppFooter = props=>{

    
    var ref = useRef({})
    
    useEffect(()=>{
        let footer = ref.current['footer']
        let document_scrollHeight = document.documentElement.scrollHeight
        footer.style.top = `${document_scrollHeight}px`

        console.log(footer, document_scrollHeight)

        footer.addEventListener('scroll' , (event)=>{
            console.log(event)
        })
    },
    [ref])

    


    return (
        <div className="footer" ref = {el=>ref.current['footer']= el } >

        </div>
    )
}

export default AppFooter;
