import './statics/css/appSignup.css'

var AppSignup = props=>{
    function checkUsername(event) {
        if (event.nativeEvent.data === '"' || event.nativeEvent.data === "'"){
            event.target.value =  event.target.value.slice(0,event.target.value.length-1)
        }
        event.target.setCustomValidity('')
    }
    return(
      <div className="signup">
          <div className="signup_form">
              <h1 className = "signup_heading">Sign Up</h1>
              <input type="file" accept= "image/*" value />
                <form action="javascript:void(0);">
                    <input type="text" className= 'signup_username' onInput= {event=>checkUsername(event)} />
                    <input type= "email" className= "signup_email" />
                    <input type="password" className= 'signup_password'  />
                    <input type="submit" value="Sign Up" />

                </form>
          </div>
      </div>
    )
}

export default AppSignup
